SELECT 

FROM test_data_main a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.user_id = #{user.id}



		AND lower(a.name) LIKE lower(#{pattern})


		AND a.sex = #{sex}




		ORDER BY ${page.orderBy}


		ORDER BY a.id DESC

SELECT 

FROM test_data_main a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.id DESC

mapper.update:
